const container_remain = flow.get("container_remain")
const plate_remain = flow.get("plate_remain")
const amount_to_feed = flow.get("amount")

if(container_remain < amount_to_feed){
    msg.payload = {
        failure: "Your food container doesn't have enough food!"
    }
}
else if(1000 - plate_remain < amount_to_feed){
    msg.payload = {
        failure: "Your plate can't hold that much food!"
    }
}
else{
    msg.payload = {
        success: "Preparing to feed your pet!"
    }
}
return msg;

