const old_percentage = flow.get("container_remain_percentage")

const new_percentage = msg.payload.percentage

// If the amount of food drops
if(new_percentage < old_percentage){
    if(new_percentage <= 50){
        if(flow.get("noti_50") === false){
            msg.payload = {
                notif: "Your food container is below 50%"
            }
            flow.set("noti_50", true)
        }
    }
    if (new_percentage <= 20) {
        if(flow.get("noti_20") === false){
            msg.payload = {
                notif: "Your food container is below 20%"
            }
            flow.set("noti_20", true)
        }
    }
    if (new_percentage == 0) {
        if(flow.get("noti_0") === false){
            msg.payload = {
                notif: "Your food container is empty, go back and fill it!!!"
            }
            flow.set("noti_0", true)
        }
    }
}
else{
    if(new_percentage > 0){
        if(flow.get("noti_0") === true){
            flow.set("noti_0", false)
        }
    }
    if(new_percentage > 20){
        if(flow.get("noti_20") === true){
            flow.set("noti_20", false)
        }
    }
    if(new_percentage > 50){
        if(flow.get("noti_50") === true){
            flow.set("noti_50", false)
        }
    }
}

flow.set("container_remain_percentage", new_percentage)

return msg;

